'use strict';

const fs = require('fs');
const step = require('h5.step');
const gm = require('gm');

module.exports = (app, module, req, res, next) =>
{
  step(
    function()
    {
      gm(req.file.path)
        .resize(500, 500, '>')
        .quality(75)
        .toBuffer('webp', this.next());
    },
    function(err, buffer)
    {
      fs.unlink(req.file.path, () => {});

      if (err)
      {
        return next(err);
      }

      res.json({
        src: 'data:image/webp;base64,' + buffer.toString('base64')
      });
    }
  );
};
