'use strict';

exports.name = 'WhEvent';

exports.setUp = (app, mongoose) =>
{
  const eventSchema = new mongoose.Schema({
    type: {
      type: String,
      required: true
    },
    time: {
      type: Date,
      required: true
    },
    user: {
      type: {},
      required: true
    },
    objects: [String],
    data: {}
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  eventSchema.statics.TOPIC_PREFIX = 'wh.events';
  eventSchema.statics.BROWSE_LIMIT = 100;

  eventSchema.index({time: -1});
  eventSchema.index({type: 1, time: -1});
  eventSchema.index({objects: 1, time: -1});
  eventSchema.index({'user.id': 1, time: -1});

  mongoose.model(exports.name, eventSchema);
};
