'use strict';

exports.name = 'WhLine';

exports.setUp = (app, mongoose) =>
{
  const lineSchema = new mongoose.Schema({
    _id: String,
    redirLine: {type: String, default: null},
    pickup: {
      sets: {type: Number, default: 0},
      qty: {type: Number, default: 0},
      time: {type: Number, default: 0}
    },
    delivery: {
      qty: {type: Number, default: 0},
      time: {type: Number, default: 0}
    }
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  lineSchema.statics.TOPIC_PREFIX = 'wh.lines';
  lineSchema.statics.BROWSE_LIMIT = 0;

  mongoose.model(exports.name, lineSchema);
};
