'use strict';

exports.name = 'WhLineGroup';

exports.setUp = (app, mongoose) =>
{
  const lineGroupSchema = new mongoose.Schema({
    _id: String,
    lines: [String]
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  lineGroupSchema.statics.TOPIC_PREFIX = 'wh.lineGroups';
  lineGroupSchema.statics.BROWSE_LIMIT = 100;

  lineGroupSchema.index({lines: 1});

  mongoose.model(exports.name, lineGroupSchema);
};
