'use strict';

const moment = require('moment');
const exportOrdersRoute = require('./exportOrders');
const updateOrderRoute = require('./updateOrder');

module.exports = (app, module) =>
{
  const {
    updater,
    settings,
    express,
    user,
    WiringEvent,
    WiringOrder
  } = module;

  const canView = user.auth('LOCAL', 'WIRING:VIEW');
  const canUpdate = user.auth('LOCAL', 'WIRING:WIRER', 'WIRING:MANAGE');
  const canManage = user.auth('WIRING:MANAGE');

  // Apps
  ['wiring'].forEach(appId =>
  {
    express.get(`/${appId}`, canView, (req, res) =>
    {
      res.format({
        'text/html': () =>
        {
          res.render('index', updater.getAppTemplateData(appId, req));
        }
      });
    });
  });

  // Settings
  express.get('/wiring/settings', canView, settings.browseRoute('wiring'));

  express.put('/wiring/settings/:id', canManage, settings.updateRoute);

  // Queues
  express.post('/wiring/:id;generate', canManage, generateRoute);

  // Events
  express.get(
    '/wiring/events',
    canView,
    express.crud.browseRoute.bind(null, app, WiringEvent)
  );

  // Orders
  express.get(
    '/wiring/orders',
    canView,
    prepareCurrentDate,
    express.crud.browseRoute.bind(null, app, WiringOrder)
  );

  express.get('/wiring/orders;export.:format?', canView, exportOrdersRoute.bind(null, app, module));

  express.get('/wiring/orders/:id', canView, express.crud.readRoute.bind(null, app, WiringOrder));

  express.patch('/wiring/orders/:id', canUpdate, updateOrderRoute.bind(null, app, module));

  function prepareCurrentDate(req, res, next)
  {
    req.rql.selector.args.forEach(term =>
    {
      if (term.name !== 'eq' || term.args[0] !== 'date')
      {
        return;
      }

      const date = term.args[1];

      if (date === 'current')
      {
        const m = moment();

        if (m.hours() < 17)
        {
          m.startOf('day').subtract(1, 'days');
        }
        else
        {
          m.startOf('day').add(1, 'days');
        }

        term.args[1] = moment.utc(m.format('YYYY-MM-DD'), 'YYYY-MM-DD').toDate();
      }
      else if (/^[0-9]+-[0-9]+-[0-9]+$/.test(date))
      {
        term.args[1] = moment.utc(date, 'YYYY-MM-DD').toDate();
      }
    });

    next();
  }

  function generateRoute(req, res)
  {
    app.broker.publish('wiring.generator.requested', {
      date: req.params.id
    });

    res.sendStatus(204);
  }
};
