'use strict';

exports.name = 'XiconfClientSettings';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    _id: String,
    updatedAt: Date,
    settings: {}
  }, {
    id: false
  });

  schema.statics.BROWSE_LIMIT = 1000;

  return schema;
};
