'use strict';

const resolveIpAddress = require('util/resolveIpAddress');

module.exports = (app, xiconfModule, req, res, next) =>
{
  const sio = app[xiconfModule.config.sioId];
  const mongoose = app[xiconfModule.config.mongooseId];
  const XiconfClient = mongoose.model('XiconfClient');

  XiconfClient.findById(req.params.id, {socket: 1, httpPort: 1}).lean().exec((err, xiconfClient) =>
  {
    if (err)
    {
      return next(err);
    }

    if (!xiconfClient)
    {
      return next(app.createError('CLIENT_NOT_FOUND', 404));
    }

    const socket = sio.sockets.connected[xiconfClient.socket];
    const httpPort = xiconfClient.httpPort || 1337;
    const remoteAddress = socket ? resolveIpAddress(socket) : xiconfClient._id.split('-')[0];

    res.redirect(`http://${remoteAddress}:${httpPort}/#${(req.query.page || '')}`);
  });
};
